/**
 * Sök aktiviteter baserat på kategori och sökterm
 * Stödjer sökning på aktivitetskod, kategori, aktivitetsbeskrivning och intensitet
 */
export function searchActivities(searchTerm: string, category?: string): METActivity[] {
  const term = searchTerm.toLowerCase().trim();

  return MET_ACTIVITIES.filter(activity => {
    // Filtrera först på kategori om specificerad
    if (category && category !== 'All' && activity.category !== category) return false;

    // Om inget sökord, returnera alla (eller alla i kategorin)
    if (!term) return true;

    // Sök på aktivitetskod
    if (activity.id.includes(term)) return true;

    // Sök på kategorinamn
    if (activity.category.toLowerCase().includes(term)) return true;

    // Sök på aktivitetsbeskrivning
    if (activity.activity.toLowerCase().includes(term)) return true;

    // Sök på intensitetsnivå
    if (activity.intensity && activity.intensity.toLowerCase().includes(term)) return true;

    return false;
  });
}

/**
 * Få alla aktiviteter för en kategori
 */
export function getActivitiesByCategory(category: string): METActivity[] {
  return MET_ACTIVITIES.filter(activity => activity.category === category);
}

/**
 * Hitta aktivitet per ID
 */
export function getActivityById(id: string): METActivity | undefined {
  return MET_ACTIVITIES.find(activity => activity.id === id);
}

/**
 * Få aktiviteter sorterade efter MET värde
 */
export function getActivitiesByMET(ascending: boolean = false): METActivity[] {
  return [...MET_ACTIVITIES].sort((a, b) => (ascending ? a.met - b.met : b.met - a.met));
}
